import React, { useState, useEffect } from 'react';
import { BASE_URL } from '../utils/config.js';
import '../styles/admin.css';

const EditBooking = () => {
    const [booking, setBooking] = useState([]);

    useEffect(() => {
        const fetchBooking = async () => {
            try {
                const res = await fetch(`${BASE_URL}/booking`, {
                    method: 'GET',
                    credentials: 'include',
                });

                if (!res.ok) {
                    throw new Error('Failed to fetch booking tours');
                }

                const result = await res.json();
                setBooking(result.data);
            } catch (error) {
                console.error(error.message);
            }
        };

        fetchBooking();
    }, []);

    return (
        <div>
            <div className='admin-header'>
                <h2>Booking</h2>
            </div>
            <div className='admin-content px-3'>
                {booking.map((tour) => (
                    <div key={tour.id}>
                        <h2>Booking Details</h2>
                        <p>User ID: {tour.userId}</p>
                        <p>User Email: {tour.userEmail}</p>
                        <p>Tour Name: {tour.tourName}</p>
                        <p>Full Name: {tour.fullName}</p>
                        <p>Guest Size: {tour.guestSize}</p>
                        <p>Phone: {tour.phone}</p>
                        <p>Booked At: {tour.bookAt}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default EditBooking;
