// ServiceFeeDecorator.js
class ServiceFeeDecorator {
    constructor(booking) {
        this.booking = booking;
    }

    calculateTotal() {
        const price = this.booking.price;
        const serviceFee = 0.1 * price;
        return this.booking.calculateTotal() + serviceFee;
    }
}

export default ServiceFeeDecorator;
