import React, { useState, useEffect } from 'react';
import { BASE_URL } from '../utils/config.js';
import '../styles/admin.css';

const EditBooking = ({ match }) => {
    const [booking, setBooking] = useState(null);
    const bookingId = match.params.id;

    useEffect(() => {
        const fetchBooking = async () => {
            try {
                const response = await fetch(`${BASE_URL}/booking`);
                if (!response.ok) {
                    throw new Error('Failed to fetch booking');
                }
                const bookingData = await response.json();
                setBooking(bookingData.data);
            } catch (error) {
                console.error('Error fetching booking:', error);
            }
        };

        fetchBooking(); // Call the function to fetch booking data
    }, [bookingId]); // Run the effect whenever the bookingId changes

    return (
        <div>
            <div className='admin-header'>
                <h2>Booking</h2>
            </div>
            <div className='admin-content px-3'>
                {booking ? (
                    <div>
                        <h2>Booking Details</h2>
                        <p>User ID: {booking.userId}</p>
                        <p>User Email: {booking.userEmail}</p>
                        <p>Tour Name: {booking.tourName}</p>
                        <p>Full Name: {booking.fullName}</p>
                        <p>Guest Size: {booking.guestSize}</p>
                        <p>Phone: {booking.phone}</p>
                        <p>Booked At: {booking.bookAt}</p>
                    </div>
                ) : (
                    <p>Loading...</p>
                )}
            </div>
        </div>
    );
};

export default EditBooking;
