import React from 'react';
const { price, reviews, title } = tour
class ServiceFeeDecorator {
    constructor(WrappedComponent) {
        this.WrappedComponent = WrappedComponent;
    }

    calculateServiceFee(price) {
        return 0.1 * price;
    }

    decorateComponent = props => {
        const serviceFee = this.calculateServiceFee(props.tour.price);

        return <this.WrappedComponent {...props} serviceFee={serviceFee} />;
    };
}

export default ServiceFeeDecorator;
