import React, { useState, useEffect } from 'react';
import '../styles/admin.css';
import { BASE_URL } from '../utils/config.js';

const EditBooking = () => {
    const [bookedTours, setBookedTours] = useState([]);

    useEffect(() => {
        // Fetch booked tours when component mounts
        const fetchBookedTours = async () => {
            try {
                const bookedToursData = await getBookedTours(); // Call function to get booked tours from server
                setBookedTours(bookedToursData); // Set state with fetched booked tours
            } catch (error) {
                console.error('Error fetching booked tours:', error);
            }
        };

        fetchBookedTours(); // Invoke the function to fetch booked tours
    }, []);

    return (
        <div>
            <div className='admin-header'>
                <h2>Booking</h2>
            </div>
            <div className='admin-content px-3'>
                <h2>Booked Tours:</h2>
                <ul>
                    {bookedTours.map((tour, index) => (
                        <li key={index}>{tour.name}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default EditBooking;
