import React, { useEffect, useState } from 'react';
import { BASE_URL } from '../utils/config.js';

import '../styles/admin.css'

const EditTour = () => {
    const [editTour, setEditTours] = useState([]);

    useEffect(() => {
        const fetchEditTours = async () => {
            try {
                const res = await fetch(`${BASE_URL}/booking`, {
                    method: 'GET',
                    credentials: 'include',
                });

                if (!res.ok) {
                    throw new Error('Failed to fetch booked tours');
                }

                const result = await res.json();
                setEditTours(result.data);
            } catch (error) {
                console.error(error.message);
            }
        };

        fetchEditTours();
    }, []);

    return (
        <div>
            <div className='admin-header'>
                <h2>Tour</h2>
            </div>
            <div className='admin-content px-3'>
                <ul>
                    {editTour.map((tour) => (
                        <li key={tour._id}>
                            <p>Tour Name: {tour.tourName}</p>
                            <p>Full Name: {tour.fullName}</p>
                            <p>Guest Size: {tour.guestSize}</p>
                            <p>Phone: {tour.phone}</p>
                            <p>Booking Date: {new Date(tour.bookAt).toLocaleDateString()}</p>
                            <button className="cancelButton" onClick={() => handleCancelTour(tour._id)}>Há»§y tour</button>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    )
}
export default EditTour
