import React, { useEffect, useState } from 'react';
import { BASE_URL } from '../utils/config.js';
import '../styles/admin.css';

const EditTour = () => {
    const [tours, setTours] = useState([]);

    useEffect(() => {
        const fetchTours = async () => {
            try {
                const res = await fetch(`${BASE_URL}/tours`, {
                    method: 'GET',
                    credentials: 'include',
                });

                if (!res.ok) {
                    throw new Error('Failed to fetch tours');
                }

                const result = await res.json();
                setTours(result.data);
            } catch (error) {
                console.error(error.message);
            }
        };

        fetchTours();
    }, []);

    return (
        <div>
            <div className='admin-header'>
                <h2>Tours</h2>
            </div>
            <div className='admin-content px-3'>
                <ul>
                    {tours.map((tour) => (
                        <li key={tour._id}>
                            <p>Tour ID: {tour._id}</p>
                            <p>Title: {tour.title}</p>
                            <p>City: {tour.city}</p>
                            <p>Address: {tour.address}</p>
                            <p>Distance: {tour.distance}</p>
                            <p>Photo: {tour.photo}</p>
                            <p>Description: {tour.desc}</p>
                            <p>Price: {tour.price}</p>
                            <p>Max Group Size: {tour.maxGroupSize}</p>
                            <p>Featured: {tour.featured ? 'Yes' : 'No'}</p>
                            <p>Created At: {new Date(tour.createdAt).toLocaleDateString()}</p>
                            <p>Updated At: {new Date(tour.updatedAt).toLocaleDateString()}</p>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default EditTour;
