import React, { useEffect, useState } from 'react';
import { BASE_URL } from '../utils/config.js';
import '../styles/admin.css';

const EditTour = () => {
    const { id } = useParams(); // Extract the tour ID from the URL params
    const [tour, setTour] = useState(null); // Initialize tour state to null

    useEffect(() => {
        const fetchTour = async () => {
            try {
                const res = await fetch(`${BASE_URL}/tours/${id}`, { // Fetch the tour by ID
                    method: 'GET',
                    credentials: 'include',
                });

                if (!res.ok) {
                    throw new Error('Failed to fetch tour');
                }

                const result = await res.json();
                setTour(result.data); // Set the fetched tour to state
            } catch (error) {
                console.error(error.message);
            }
        };

        fetchTour();
    }, [id]); // Trigger useEffect whenever the tour ID changes

    return (
        <div>
            <div className='admin-header'>
                <h2>Edit Tour</h2>
            </div>
            <div className='admin-content px-3'>
                {tour ? ( // Render tour details if tour data is available
                    <div>
                        <h3>{tour.title}</h3>
                        <p>City: {tour.city}</p>
                        <p>Address: {tour.address}</p>
                        {/* Render other tour details as needed */}
                    </div>
                ) : (
                    <p>Loading...</p> // Display loading message while fetching tour data
                )}
            </div>
        </div>
    );
}

export default EditTour;
