import React, { useState, useEffect } from 'react';
import axios from 'axios';

import '../styles/admin.css';

const EditTour = () => {
    const [tours, setTours] = useState([]);
    const [selectedTour, setSelectedTour] = useState(null);
    const [tourData, setTourData] = useState({
        name: '',
        description: '',
        // Thêm các trường dữ liệu khác tùy thuộc vào cấu trúc của tour
    });

    useEffect(() => {
        // Gọi API để lấy danh sách tour khi component được tạo
        fetchTours();
    }, []);

    const fetchTours = async () => {
        try {
            const response = await axios.get('/api/tours');
            setTours(response.data.data);
        } catch (error) {
            console.error('Error fetching tours:', error);
        }
    };

    const handleTourSelect = (tour) => {
        setSelectedTour(tour);
        setTourData({
            name: tour.name,
            description: tour.description,
            // Set các trường dữ liệu khác tùy thuộc vào cấu trúc của tour
        });
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setTourData({ ...tourData, [name]: value });
    };

    const handleFormSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`/api/tours/${selectedTour._id}`, tourData);
            // Cập nhật lại danh sách tour sau khi cập nhật thành công
            fetchTours();
            // Clear form data và selectedTour
            setTourData({
                name: '',
                description: '',
                // Clear các trường dữ liệu khác
            });
            setSelectedTour(null);
        } catch (error) {
            console.error('Error updating tour:', error);
        }
    };

    return (
        <div>
            <div className='admin-header'>
                <h2>Tour</h2>
            </div>
            <div className='admin-content px-3'>
                <div className="tour-list">
                    <h3>Danh sách các tour</h3>
                    <ul>
                        {tours.map(tour => (
                            <li key={tour._id} onClick={() => handleTourSelect(tour)}>
                                {tour.name}
                            </li>
                        ))}
                    </ul>
                </div>
                {selectedTour && (
                    <div className="tour-details">
                        <h3>Chỉnh sửa thông tin tour</h3>
                        <form onSubmit={handleFormSubmit}>
                            <label htmlFor="name">Tên tour:</label>
                            <input type="text" name="name" value={tourData.name} onChange={handleInputChange} />
                            <label htmlFor="description">Mô tả:</label>
                            <textarea name="description" value={tourData.description} onChange={handleInputChange}></textarea>
                            {/* Thêm các trường dữ liệu khác vào biểu mẫu */}
                            <button type="submit">Cập nhật</button>
                        </form>
                    </div>
                )}
            </div>
        </div>
    );
};

export default EditTour;
