import React, { useState, useContext } from 'react';
import "./booking.css";
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from 'reactstrap';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import { BASE_URL } from '../../utils/config';
import FeeDecorator from './feeDecorator'; // Import decorator

class Booking extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            booking: {
                userId: null,
                userEmail: null,
                tourName: this.props.tour.title,
                fullName: "",
                phone: '',
                guestSize: 1,
                bookAt: ''
            }
        };
        this.navigate = useNavigate();
        this.user = useContext(AuthContext).user;
    }

    handleChange = e => {
        this.setState(prevState => ({
            booking: {
                ...prevState.booking,
                [e.target.id]: e.target.value
            }
        }));
    }

    handleClick = async e => {
        e.preventDefault();

        console.log(this.state.booking);

        try {
            if (!this.user || this.user === undefined || this.user === null) {
                return alert('Vui lòng đăng nhập');
            }

            const res = await fetch(`${BASE_URL}/booking`, {
                method: 'post',
                headers: {
                    'content-type': 'application/json'
                },
                credentials: 'include',
                body: JSON.stringify(this.state.booking)
            });

            const result = await res.json();

            if (!res.ok) {
                return alert(result.message);
            }
            this.navigate("/thank-you");

        } catch (err) {
            alert(err.message);
        }
    }

    calculateTotalPrice = () => {
        const feeDecorator = new FeeDecorator({ calculatePrice: () => Number(this.props.tour.price) * Number(this.state.booking.guestSize) });
        return feeDecorator.calculatePrice();
    }

    render() {
        const { tour, avgRating } = this.props;
        const { booking } = this.state;

        return (
            <div className="booking">
                <div className="booking__top d-flex align-items-center justify-content-between">
                    <h3>{tour.price}đ <span> /người</span></h3>
                    <span className="tour__rating d-flex align-items-center ">
                        <i className="ri-star-fill" > </i>
                        {avgRating === 0 ? null : avgRating} ({tour.reviews?.length})
                    </span>
                </div>

                <div className="booking__form">
                    <h5>Thông tin</h5>
                    <Form className="booking__info-form" onSubmit={this.handleClick}>
                        <FormGroup>
                            <input type="text" placeholder="Họ và tên" id="fullName" required onChange={this.handleChange} />
                        </FormGroup>
                        <FormGroup>
                            <input type="number" placeholder="Số điện thoại" id="phone" required onChange={this.handleChange} />
                        </FormGroup>
                        <FormGroup className="d-flex align-items-center gap-3">
                            <input type="date" placeholder="" id="bookAt" required onChange={this.handleChange} />
                            <input type="number" placeholder="Số khách" id="guestSize" required onChange={this.handleChange} />
                        </FormGroup>
                    </Form>
                </div>

                <div className="booking__bottom">
                    <ListGroup>
                        <ListGroupItem className="border-0 px-0">
                            <h5 className="d-flex align-items-center gap-1">
                                {tour.price}đ <i className="ri-close-line"></i> 1 người
                            </h5>
                            <span>{tour.price}đ</span>
                        </ListGroupItem>
                        <ListGroupItem className="border-0 px-0">
                            <h5>Phí dịch vụ</h5>
                            <span>Calculated by decorator</span>
                        </ListGroupItem>
                        <ListGroupItem className="border-0 px-0">
                            <h5>Phí bảo hiểm</h5>
                            <span>Calculated by decorator</span>
                        </ListGroupItem>
                        <ListGroupItem className="border-0 px-0 total">
                            <h5>Tổng</h5>
                            <span>{this.calculateTotalPrice()}đ</span>
                        </ListGroupItem>
                    </ListGroup>

                    <Button className="btn primary__btn w-100 mt-4" onClick={this.handleClick}>
                        Đặt ngay
                    </Button>
                </div>
            </div>
        );
    }
}

export default Booking;
