const { price, reviews, title } = tour;
class BookingFeeDecorator {
    constructor(price) {
        this.price = price;
    }

    calculateService() {
        return this.price * 0.1;
    }

    calculateInsurance() {
        return this.price * 0.05;
    }

    getTotalAmount() {
        const serviceFee = this.calculateService();
        const insuranceFee = this.calculateInsurance();
        const totalAmount = Number(this.price) * Number(this.guestSize) + Number(serviceFee) + Number(insuranceFee);
        return totalAmount;
    }
}

export default BookingFeeDecorator;
