import React, { useState, useContext } from 'react';
import "./booking.css";
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from 'reactstrap';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import { BASE_URL } from '../../utils/config';
import PayPalCheckout from './PayPalCheckout'; // ThÃªm import cho PayPalCheckout component
import withFees from './FeeHOC';

const Booking = ({ tour, avgRating, serviceFee, insuranceFee }) => {
    const { price, reviews, title } = tour;
    const navigate = useNavigate();
    const { user } = useContext(AuthContext);

    const [booking, setBooking] = useState({
        userId: user && user._id,
        userEmail: user && user.email,
        tourName: title,
        fullName: "",
        phone: '',
        guestSize: 1,
        bookAt: ''
    });

    const handleChange = e => {
        setBooking(prev => ({ ...prev, [e.target.id]: e.target.value }));
    }

    const totalAmount = Number(price) * Number(booking.guestSize) + serviceFee + insuranceFee;

    const handleBookingSuccess = async () => {
        try {
            const bookingData = {
                ...booking,
                userId: user._id,
                userEmail: user.email,
            };

            const res = await fetch(`${BASE_URL}/booking`, {
                method: 'post',
                headers: {
                    'content-type': 'application/json'
                },
                credentials: 'include',
                body: JSON.stringify(bookingData)
            });

            const result = await res.json();

            if (!res.ok) {
                return alert(result.message);
            }
            navigate("/thank-you");

        } catch (err) {
            alert(err.message);
        }
    }

    return (
        <div className="booking">
            <div className="booking__top d-flex align-items-center justify-content-between">
                <h3>{price}Ä <span> /ngÆ°á»i</span></h3>
                <span className="tour__rating d-flex align-items-center ">
                    <i className="ri-star-fill" > </i>
                    {avgRating === 0 ? null : avgRating} ({reviews?.length})
                </span>
            </div>

            <div className="booking__form">
                <h5>ThÃ´ng tin</h5>
                <Form className="booking__info-form">
                    <FormGroup>
                        <input type="text" placeholder="Há» vÃ  tÃªn" id="fullName" required onChange={handleChange} />
                    </FormGroup>
                    <FormGroup>
                        <input type="number" placeholder="Sá» Äiá»n thoáº¡i" id="phone" required onChange={handleChange} />
                    </FormGroup>
                    <FormGroup className="d-flex align-items-center gap-3">
                        <input type="date" placeholder="" id="bookAt" required onChange={handleChange} />
                        <input type="number" placeholder="Sá» khÃ¡ch" id="guestSize" required onChange={handleChange} />
                    </FormGroup>
                </Form>
            </div>

            <div className="booking__bottom">
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5 className="d-flex align-items-center gap-1">
                            {price}Ä <i className="ri-close-line"></i> 1 ngÆ°á»i
                        </h5>
                        <span>{price}Ä</span>
                    </ListGroupItem>
                    <ListGroupItem className="border-0 px-0">
                        <h5>PhÃ­ dá»ch vá»¥</h5>
                        <span>{serviceFee}Ä</span>
                    </ListGroupItem>
                    <ListGroupItem className="border-0 px-0">
                        <h5>PhÃ­ báº£o hiá»m</h5>
                        <span>{insuranceFee}Ä</span>
                    </ListGroupItem>
                    <ListGroupItem className="border-0 px-0 total">
                        <h5>Tá»ng</h5>
                        <span>{totalAmount}Ä</span>
                    </ListGroupItem>
                </ListGroup>

                {/* Thay tháº¿ nÃºt "Äáº·t ngay" báº±ng PayPalCheckout */}
                <PayPalCheckout totalAmount={totalAmount} tour={tour} onSuccess={handleBookingSuccess} />
            </div>
        </div>
    )
}

export default withFees(Booking);
