import React, { useEffect, useState } from 'react';
import { BASE_URL } from '../utils/config.js';
import '../styles/admin.css';

const EditTour = () => {
    const [tour, setTour] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [newTourData, setNewTourData] = useState({
        title: '',
        city: '',
        address: '',
        distance: '',
        photo: '',
        desc: '',
        price: '',
        maxGroupSize: '',
        featured: false
    });

    useEffect(() => {
        const fetchTour = async () => {
            try {
                const res = await fetch(`${BASE_URL}/tours`, {
                    method: 'GET',
                    credentials: 'include',
                });

                if (!res.ok) {
                    throw new Error('Failed to fetch tours');
                }

                const result = await res.json();
                setTour(result.data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchTour();
    }, []);

    const handleAddTour = async () => {
        try {
            const res = await fetch(`${BASE_URL}/tours`, {
                method: 'POST',
                credentials: 'include',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(newTourData)
            });

            if (!res.ok) {
                throw new Error('Failed to add tour');
            }

            const newTour = await res.json();
            setTour([...tour, newTour]);
            setNewTourData({
                title: '',
                city: '',
                address: '',
                distance: '',
                photo: '',
                desc: '',
                price: '',
                maxGroupSize: '',
                featured: false
            });
        } catch (error) {
            setError(error.message);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewTourData({
            ...newTourData,
            [name]: value
        });
    };

    return (
        <div>
            <div className='admin-header button-container'>
                <h2>Tour</h2>
                <button className="add-button" onClick={handleAddTour}>Thêm</button>
            </div>
            <div className='admin-content px-3'>
                {/* Existing Tour List */}
                {/* ... */}
            </div>
            <div className="add-tour-form">
                <h3>Thêm Tour Mới</h3>
                <input type="text" name="title" placeholder="Tiêu đề" value={newTourData.title} onChange={handleInputChange} />
                {/* Add other input fields for tour details */}
                <button onClick={handleAddTour}>Thêm Tour</button>
                {error && <p className="error-message">{error}</p>}
            </div>
        </div>
    );
};

export default EditTour;
