import React, { useState, useEffect } from 'react';
import '../styles/admin.css';
import { getBookedTours } from '../api/bookedTours'; // Import hàm lấy danh sách tour đã đặt từ backend

const EditBooking = () => {
    const [bookedTours, setBookedTours] = useState([]); // State để lưu trữ danh sách các tour đã đặt

    useEffect(() => {
        // Gọi hàm lấy danh sách các tour đã đặt từ backend khi component được render
        const fetchBookedTours = async () => {
            try {
                const bookedToursData = await getBookedTours(); // Gọi hàm lấy danh sách tour đã đặt từ backend
                setBookedTours(bookedToursData); // Cập nhật state với danh sách tour đã đặt từ backend
            } catch (error) {
                console.error('Error fetching booked tours:', error);
            }
        };

        fetchBookedTours(); // Gọi hàm lấy danh sách tour đã đặt
    }, []); // useEffect sẽ chỉ được gọi một lần khi component được render

    return (
        <div>
            <div className='admin-header'>
                <h2>Booking</h2>
            </div>
            <div className='admin-content px-3'>
                <h2>Các tour đã đặt:</h2>
                <ul>
                    {bookedTours.map((tour, index) => (
                        <li key={index}>{tour.name}</li> // Thay thế `tour.name` bằng thuộc tính tương ứng của mỗi tour
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default EditBooking;
