        } catch (error) {
            setError(error.message);
        }
    };

    return (
        <div>
            <div className='admin-header'>
                <h2>User</h2>
            </div>
            <div className='admin-content px-3'>
                {loading ? (
                    <h2>Đang tải...</h2>
                ) : (
                    <ul>
                        {user.map(user => (
                            <li key={user._id}>
                                <p>User ID: {user._id}</p>
                                <p>Username: {user.username}</p>
                                <p>Email: {user.email}</p>
                                <p>Password: {user.password}</p>
                                <p>Role: {user.role}</p>
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
};

export default EditUser;
