import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/admin.css';

const EditBooking = () => {
    const { id } = useParams();
    const [booking, setBooking] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchBooking = async () => {
            try {
                const response = await fetch(`/api/booking`);
                if (!response.ok) {
                    throw new Error('Failed to fetch booking');
                }
                const data = await response.json();
                setBooking(data.data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching booking:', error);
            }
        };

        fetchBooking();
    }, [id]);

    return (
        <div>
            <div className='admin-header'>
                <h2>Edit Booking</h2>
            </div>
            <div className='admin-content px-3'>
                {loading ? (
                    <p>Loading...</p>
                ) : booking ? (
                    <>
                        <h3>Booking ID: {booking._id}</h3>
                        <p>User ID: {booking.userId}</p>
                        <p>User Email: {booking.userEmail}</p>
                        <p>Tour Name: {booking.tourName}</p>
                        <p>Full Name: {booking.fullName}</p>
                        <p>Guest Size: {booking.guestSize}</p>
                        <p>Phone: {booking.phone}</p>
                        <p>Booked At: {new Date(booking.bookAt).toLocaleString()}</p>
                    </>
                ) : (
                    <p>No booking found with ID: {id}</p>
                )}
            </div>
        </div>
    );
};

export default EditBooking;
