class TourComponent {
    getPrice() { }
}

class BasicTour extends TourComponent {
    constructor(price) {
        super();
        this.price = price;
    }

    getPrice() {
        return this.price;
    }
}

class FeeDecorator extends TourComponent {
    constructor(tourComponent) {
        super();
        this.tourComponent = tourComponent;
    }

    getPrice() {
        return this.tourComponent.getPrice();
    }
}

class ServiceFeeDecorator extends FeeDecorator {
    getPrice() {
        return super.getPrice() * 1.1;
    }
}

class InsuranceFeeDecorator extends FeeDecorator {
    getPrice() {
        return super.getPrice() * 1.05;
    }
}

const tourWithServiceFee = new ServiceFeeDecorator(basicTour);
console.log(tourWithServiceFee.getPrice()); // Output: 110 (Base price + service fee)

const tourWithInsuranceFee = new InsuranceFeeDecorator(basicTour);
console.log(tourWithInsuranceFee.getPrice()); // Output: 105 (Base price + insurance fee)

const tourWithBothFees = new InsuranceFeeDecorator(new ServiceFeeDecorator(basicTour));
console.log(tourWithBothFees.getPrice()); // Output: 115 (Base price + service fee + insurance fee)
