import React, { useEffect, useState } from 'react';
import { BASE_URL } from '../utils/config.js';
import '../styles/admin.css';

const EditTour = () => {
    const [tour, setTour] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [newTourData, setNewTourData] = useState({
        title: '',
        city: '',
        address: '',
        distance: '',
        photo: '',
        desc: '',
        price: '',
        maxGroupSize: '',
        featured: false
    });

    useEffect(() => {
        const fetchTour = async () => {
            try {
                const res = await fetch(`${BASE_URL}/tours`, {
                    method: 'GET',
                    credentials: 'include',
                });

                if (!res.ok) {
                    throw new Error('Failed to fetch tours');
                }

                const result = await res.json();
                setTour(result.data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchTour();
    }, []);

    const handleAddTour = async () => {
        try {
            const res = await fetch(`${BASE_URL}/tours`, {
                method: 'POST',
                credentials: 'include',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(newTourData)
            });

            if (!res.ok) {
                throw new Error('Failed to add tour');
            }

            const newTour = await res.json();
            setTour([...tour, newTour]);
            setNewTourData({
                title: '',
                city: '',
                address: '',
                distance: '',
                photo: '',
                desc: '',
                price: '',
                maxGroupSize: '',
                featured: false
            });
        } catch (error) {
            setError(error.message);
        }
    };

    const handleDeleteTour = async (id) => {
        try {
            await fetch(`${BASE_URL}/tours/${id}`, {
                method: 'DELETE',
                credentials: 'include',
            });

            setTour(tour.filter(tour => tour._id !== id));
        } catch (error) {
            setError(error.message);
        }
    };

    return (
        <div>
            <div className='admin-header button-container'>
                <h2>Tour</h2>
                <button className="add-button" onClick={handleAddTour}>Thêm</button>
            </div>
            <div className='admin-content px-3'>
                {loading ? (
                    <h2>Đang tải...</h2>
                ) : error ? (
                    <p className="error-message">{error}</p>
                ) : tour.length === 0 ? (
                    <p>Không tìm thấy</p>
                ) : (
                    <ul>
                        {tour.map((tourItem) => (
                            <li key={tourItem._id}>
                                {/* Hiển thị thông tin tour */}
                                <p>Tour ID: {tourItem._id}</p>
                                <p>Title: {tourItem.title}</p>
                                <p>City: {tourItem.city}</p>
                                <p>Address: {tourItem.address}</p>
                                <p>Distance: {tourItem.distance}</p>
                                <p>Photo: {tourItem.photo}</p>
                                <p>Description: {tourItem.desc}</p>
                                <p>Price: {tourItem.price}</p>
                                <p>Max Group Size: {tourItem.maxGroupSize}</p>
                                <p>Featured: {tourItem.featured ? 'Yes' : 'No'}</p>
                                <button className="delete-button" onClick={() => handleDeleteTour(tourItem._id)}>Xoá</button>
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
};

export default EditTour;
