import React, { useEffect, useState } from 'react';
import { BASE_URL } from '../utils/config.js';
import '../styles/admin.css';

const EditBooking = () => {
    const [bookings, setBookings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchBooking = async () => {
            try {
                const res = await fetch(`${BASE_URL}/booking`, {
                    method: 'GET',
                    credentials: 'include',
                });

                if (!res.ok) {
                    throw new Error('Failed to fetch booking');
                }

                const result = await res.json();
                setBookings(result.data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchBooking();
    }, []);

    const handleAddBooking = async () => {
        try {
            const res = await fetch(`${BASE_URL}/booking`, {
                method: 'POST',
                credentials: 'include',
            });

            if (!res.ok) {
                throw new Error('Failed to add booking');
            }

            const newBooking = await res.json();

            setBookings([...bookings, newBooking]);
        } catch (error) {
            setError(error.message);
        }
    };

    const handleEditBooking = async (id) => {
        try {
            // Assuming you have a modal or a form to edit booking details
            // After editing, make an HTTP request to update the booking
            const res = await fetch(`${BASE_URL}/booking/${id}`, {
                method: 'PUT',
                credentials: 'include',
                // Add updated booking details in the request body
            });

            if (!res.ok) {
                throw new Error('Failed to edit booking');
            }

            // Assuming your backend returns the updated booking
            const updatedBooking = await res.json();

            // Update the booking in the state
            setBookings(bookings.map(booking => booking._id === id ? updatedBooking : booking));
        } catch (error) {
            setError(error.message);
        }
    };

    const handleDeleteBooking = async (id) => {
        try {
            await fetch(`${BASE_URL}/booking/${id}`, {
                method: 'DELETE',
                credentials: 'include',
            });

            // Remove the deleted booking from the state
            setBookings(bookings.filter(booking => booking._id !== id));
        } catch (error) {
            setError(error.message);
        }
    };

    return (
        <div>
            <div className='admin-header'>
                <h2>Booking</h2>
                <button onClick={handleAddBooking}>Add Booking</button>
            </div>
            <div className='admin-content px-3'>
                {loading ? (
                    <h2>Đang tải...</h2>
                ) : error ? (
                    <p className="error-message">{error}</p>
                ) : bookings.length === 0 ? (
                    <p>No bookings found.</p>
                ) : (
                    <ul>
                        {bookings.map((booking) => (
                            <li key={booking._id}>
                                <p>Tour ID: {booking._id}</p>
                                <p>User Email: {booking.userEmail}</p>
                                <p>Tour Name: {booking.tourName}</p>
                                <p>Full Name: {booking.fullName}</p>
                                <p>Guest Size: {booking.guestSize}</p>
                                <p>Phone: {booking.phone}</p>
                                <p>Booking Date: {new Date(booking.bookAt).toLocaleDateString()}</p>
                                <button onClick={() => handleEditBooking(booking._id)}>Edit</button>
                                <button onClick={() => handleDeleteBooking(booking._id)}>Delete</button>
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
};

export default EditBooking;
