import React, { useState } from 'react'
import { useParams } from 'react-router-dom'
import useFetch from '../hooks/useFetch'

import '../styles/admin.css'

const EditTour = () => {
    const { id } = useParams() // Assuming you're using React Router to get the tour ID from the URL
    const { data: tour, error, loading } = useFetch(`/api/tours/${id}`) // Fetch the tour data from your API

    const [formData, setFormData] = useState({
        // Initialize form data with empty values or use default values from the fetched tour data
        name: tour?.name || '',
        description: tour?.description || '',
        // Add more fields as needed
    })

    const handleChange = (e) => {
        const { name, value } = e.target
        setFormData({
            ...formData,
            [name]: value
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        // Handle form submission, update the tour data using an API call
        // You can use fetch or axios to send a PUT request to update the tour data
    }

    if (loading) {
        return <div>Loading...</div>
    }

    if (error) {
        return <div>Error: {error}</div>
    }

    return (
        <div>
            <div className='admin-header'>
                <h2>Edit Tour</h2>
            </div>
            <div className='admin-content px-3'>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="name">Tour Name:</label>
                        <input type="text" id="name" name="name" value={formData.name} onChange={handleChange} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="description">Description:</label>
                        <textarea id="description" name="description" value={formData.description} onChange={handleChange} />
                    </div>
                    {/* Add more form fields for other tour properties */}
                    <button type="submit">Save</button>
                </form>
            </div>
        </div>
    )
}

export default EditTour
