import React, { useRef, useEffect, useContext } from 'react'
import { Container, Row, Button } from 'reactstrap'
import { NavLink, Link, useNavigate } from 'react-router-dom'
import UserDropdown from './UserDropdown.jsx';

import logo from '../../assets/images/logo.png'
import './header.css';

import { AuthContext } from './../../context/AuthContext'

const nav__links = [
    {
        path: '/home',
        display: 'Trang chủ'
    },
    {
        path: '/tours',
        display: 'Du lịch'
    },
    {
        path: '/about',
        display: 'Giới thiệu'
    },
]

const Header = () => {

    const headerRef = useRef(null)
    const menuRef = useRef(null)
    const navigate = useNavigate()
    const { user, dispatch } = useContext(AuthContext)

    const logout = () => {
        dispatch({ type: 'LOGOUT' })
        navigate('/')
    }

    const stickyHeaderFunc = () => {
        window.addEventListener('scroll', () => {
            if (document.body.scrollTop > 80 || document.documentElement.scrollTop > 80) {
                headerRef.current.classList.add('sticky__header')
            } else {
                headerRef.current.classList.remove('sticky__header')
            }
        })
    }

    useEffect(() => {
        stickyHeaderFunc()

        return window.removeEventListener('scroll', stickyHeaderFunc)
    })

    const toggleMenu = () => menuRef.current.classList.toggle('show__menu')

    return <header className="header" ref={headerRef}>
        <Container>
            <Row>
                <div className="nav__wrapper d-flex align-items-center justify-content-between">
                    {/*Logo*/}
                    <div className="logo">
                        <img src={logo} alt="" />
                    </div>
                    {/*Logo end*/}

                    {/*menu start*/}
                    <div className="navtigation" ref={menuRef} onClick={toggleMenu}>
                        <ul className="menu d-flex align-items-center gap-5">
                            {
                                nav__links.map((item, index) => (
                                    <li className="nav__item" key={index}>
                                        <NavLink to={item.path} className={navClass => navClass.isActive ? 'active__link' : ''
                                        }
                                        >
                                            {item.display}
                                        </NavLink>
                                    </li>
                                ))
                            }
                        </ul>
                    </div>
                    {/*menu end*/}

                    <div className="nav__right d-flex align-items-center gap-4">
                        <div className="nav__btns d-flex align-items-center gap-4">

                            {
                                user ? <>
                                    <div className="user-info d-flex align-items-center gap-2">
                                        <UserDropdown />
                                        <h5 className="mb-0">{user.username}</h5>
                                        <Button className="btn btn-dark" onClick={logout}>Đăng xuất</Button>
                                    </div>
                                </> : <>
                                    <Button className="btn secondary__btn"><Link to='login'>Đăng nhập</Link></Button>
                                    <Button className="btn primary__btn"><Link to='register'>Đăng ký</Link></Button>
                                </>
                            }

                        </div>

                        <span className="mobile__menu" onClick={toggleMenu}>
                            <i class="ri-menu-line"></i>
                        </span>
                    </div>
                </div>
            </Row>
        </Container>
    </header>
}

export default Header
