import React, { useState, useContext } from 'react';
import "./booking.css";
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from 'reactstrap';

import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import { BASE_URL } from '../../utils/config';

// Decorator function for calculating service fee
const withServiceFee = (WrappedComponent) => {
    return (props) => {
        const { price } = props.tour;
        const serviceFee = 0.1 * price; // 10% of the price
        return <WrappedComponent {...props} serviceFee={serviceFee} />;
    };
};

// Decorator function for calculating insurance fee
const withInsuranceFee = (WrappedComponent) => {
    return (props) => {
        const { price } = props.tour;
        const insuranceFee = 0.05 * price; // 5% of the price
        return <WrappedComponent {...props} insuranceFee={insuranceFee} />;
    };
};

// Main Booking component
const Booking = ({ tour, avgRating, serviceFee, insuranceFee }) => {
    const { price, reviews, title } = tour;
    const navigate = useNavigate();
    const { user } = useContext(AuthContext);

    const [booking, setBooking] = useState({
        userId: user && user._id,
        userEmail: user && user.email,
        tourName: title,
        fullName: "",
        phone: '',
        guestSize: 1,
        bookAt: ''
    });

    const handleChange = e => {
        setBooking(prev => ({ ...prev, [e.target.id]: e.target.value }));
    }

    const totalAmount = Number(price) * Number(booking.guestSize) + Number(serviceFee) + Number(insuranceFee);

    const handleClick = async e => {
        e.preventDefault();

        console.log(booking);

        try {
            if (!user || user === undefined || user === null) {
                return alert('Vui lòng đăng nhập');
            }

            const res = await fetch(`${BASE_URL}/booking`, {
                method: 'post',
                headers: {
                    'content-type': 'application/json'
                },
                credentials: 'include',
                body: JSON.stringify(booking)
            });

            const result = await res.json();

            if (!res.ok) {
                return alert(result.message);
            }
            navigate("/thank-you");

        } catch (err) {
            alert(err.message);
        }
    }

    return (
        <div className="booking">
            <div className="booking__top d-flex align-items-center justify-content-between">
                <h3>{price}đ <span> /người</span></h3>
                <span className="tour__rating d-flex align-items-center ">
                    <i className="ri-star-fill"></i>
                    {avgRating === 0 ? null : avgRating} ({reviews?.length})
                </span>
            </div>

            <div className="booking__form">
                <h5>Thông tin</h5>
                <Form className="booking__info-form" onSubmit={handleClick}>
                    <FormGroup>
                        <input type="text" placeholder="Họ và tên" id="fullName" required onChange={handleChange} />
                    </FormGroup>
                    <FormGroup>
                        <input type="number" placeholder="Số điện thoại" id="phone" required onChange={handleChange} />
                    </FormGroup>
                    <FormGroup className="d-flex align-items-center gap-3">
                        <input type="date" placeholder="" id="bookAt" required onChange={handleChange} />
                        <input type="number" placeholder="Số khách" id="guestSize" required onChange={handleChange} />
                    </FormGroup>
                </Form>
            </div>

            <div className="booking__bottom">
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5 className="d-flex align-items-center gap-1">
                            {price}đ <i className="ri-close-line"></i> 1 người
                        </h5>
                        <span>{price}đ</span>
                    </ListGroupItem>
                    <ListGroupItem className="border-0 px-0">
                        <h5>Phí dịch vụ</h5>
                        <span>{serviceFee}đ</span>
                    </ListGroupItem>
                    <ListGroupItem className="border-0 px-0">
                        <h5>Phí bảo hiểm</h5>
                        <span>{insuranceFee}đ</span>
                    </ListGroupItem>
                    <ListGroupItem className="border-0 px-0 total">
                        <h5>Tổng</h5>
                        <span>{totalAmount}đ</span>
                    </ListGroupItem>
                </ListGroup>

                <Button className="btn primary__btn w-100 mt-4" onClick={handleClick}>
                    Đặt ngay
                </Button>
            </div>
        </div>
    )
}

// Wrap the Booking component with decorators
const DecoratedBooking = withInsuranceFee(withServiceFee(Booking));

export default DecoratedBooking;
