import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { BASE_URL } from '../utils/config';
import './edit-tour.css';

const EditTour = () => {
    const { id } = useParams();
    const [tour, setTour] = useState({
        name: '',
        description: '',
        // Add more fields as needed
    });

    useEffect(() => {
        const fetchTour = async () => {
            try {
                const response = await fetch(`${BASE_URL}/tours/${id}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch tour');
                }
                const tourData = await response.json();
                setTour(tourData.data);
            } catch (error) {
                console.error(error.message);
            }
        };

        fetchTour();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setTour(prevTour => ({
            ...prevTour,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`${BASE_URL}/tours/${id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(tour)
            });
            if (!response.ok) {
                throw new Error('Failed to update tour');
            }
            // Optionally, handle success scenario (e.g., show success message)
        } catch (error) {
            console.error(error.message);
        }
    };

    return (
        <div className="edit-tour">
            <h2>Edit Tour</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="name">Tour Name:</label>
                    <input type="text" id="name" name="name" value={tour.name} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="description">Description:</label>
                    <textarea id="description" name="description" value={tour.description} onChange={handleChange} />
                </div>
                {/* Add more form fields for other tour properties */}
                <button type="submit">Save</button>
            </form>
        </div>
    );
};

export default EditTour;
