class BookingFeeDecorator {
    constructor(tour, booking) {
        this.tour = tour;
        this.booking = booking;
    }

    calculateService() {
        return this.tour.price * 0.1;
    }

    calculateInsurance() {
        return this.tour.price * 0.05;
    }

    getTotalAmount() {
        const serviceFee = this.calculateService();
        const insuranceFee = this.calculateInsurance();
        const totalAmount = Number(this.tour.price) * Number(this.booking.guestSize) + Number(serviceFee) + Number(insuranceFee);
        return totalAmount;
    }
}

export default BookingFeeDecorator;
